.custom-node {
  background: white;
  border-radius: 12px;
  border: 2px solid #e5e7eb;
  min-width: 280px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, sans-serif;
}

.node-content {
  padding: 16px;
}

/* Lyrics Node */
.lyrics-node {
  border-color: #fecaca;
}

.lyrics-node .node-header {
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 12px 16px;
  border-bottom: 1px solid #f3f4f6;
}

.lyrics-input {
  width: 100%;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 8px;
  font-size: 13px;
  line-height: 1.5;
  resize: vertical;
  font-family: inherit;
}

.lyrics-input:focus {
  outline: none;
  border-color: #3b82f6;
}

/* Task Node (TextToText) */
.task-node {
  border-color: #86efac;
  background: #f0fdf4;
}

.task-node .node-header {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid #dcfce7;
  gap: 8px;
}

.header-left {
  display: flex;
  align-items: center;
  gap: 8px;
  flex: 1;
}

.node-icon {
  font-size: 18px;
}

.node-title {
  font-weight: 600;
  font-size: 14px;
  color: #111827;
}

.icon-btn {
  background: none;
  border: none;
  cursor: pointer;
  font-size: 14px;
  padding: 4px;
}

.close-btn {
  color: #374151;
}

.field {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}

.field-label {
  font-size: 13px;
  font-weight: 600;
  color: #111827;
}

.required {
  color: #ef4444;
}

.add-pdf-btn {
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 4px 12px;
  font-size: 12px;
  cursor: pointer;
}

.text-input {
  width: 100%;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 8px;
  font-size: 13px;
  line-height: 1.5;
  resize: vertical;
  font-family: inherit;
  margin-bottom: 12px;
}

.model-selector {
  display: flex;
  gap: 8px;
  align-items: center;
}

.add-model-btn {
  background: #374151;
  color: white;
  border: none;
  border-radius: 6px;
  padding: 6px 12px;
  font-size: 13px;
  cursor: pointer;
}

.selected-model {
  background: white;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 6px 12px;
  font-size: 13px;
  display: flex;
  align-items: center;
  gap: 8px;
}

.remove-btn {
  background: none;
  border: none;
  cursor: pointer;
  color: #ef4444;
  font-size: 16px;
  padding: 0;
}

/* Splitter Node */
.splitter-node {
  border-color: #86efac;
  background: #f0fdf4;
}

.splitter-node .node-header {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid #dcfce7;
  gap: 8px;
}

/* Image Gen Node */
.image-gen-node {
  border-color: #93c5fd;
  background: #eff6ff;
  min-width: 320px;
}

.image-gen-node .node-header {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid #dbeafe;
  gap: 8px;
}

.badge {
  background: #dbeafe;
  color: #1e40af;
  border-radius: 6px;
  padding: 4px 8px;
  font-size: 11px;
  font-weight: 600;
}

.select-input {
  width: 100%;
  border: 1px solid #e5e7eb;
  border-radius: 6px;
  padding: 8px;
  font-size: 13px;
  line-height: 1.5;
  font-family: inherit;
  margin-bottom: 12px;
}

.style-images {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 12px;
}

.style-card {
  border: 2px solid #e5e7eb;
  border-radius: 8px;
  overflow: hidden;
  cursor: pointer;
  transition: border-color 0.2s;
}

.style-card.selected {
  border-color: #3b82f6;
  box-shadow: 0 0 0 2px rgba(59, 130, 246, 0.2);
}

.style-img {
  width: 100%;
  height: 100px;
  object-fit: cover;
}

.style-name {
  padding: 8px;
  font-size: 12px;
  text-align: center;
  background: white;
  font-weight: 500;
}

/* Video Node */
.video-node {
  border-color: #fda4af;
  background: #fef2f2;
}

.video-node .node-header {
  display: flex;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid #fecaca;
  gap: 8px;
}
